export default function Signup() {
return (
    <>
      <div className="SignUp">
        <div className="left-bar">
          <div className="top">
            <div>
              <div className="logo">
                <h2>TechnoVeda</h2>
              </div>
            </div>
            <p>Signup to become a part of this journey</p>
