        </div>
        <div className="main-signup">
          <h1>Sign Up</h1>
          <div className="signup-form">
            <label htmlFor="email">Email</label>
            <input
              type="text"
              id="email"
            //   value={emailId}
            //   onChange={handleChange("emailId")}
            />

            <label htmlFor="fullname">Full Name</label>
            <input
              type="text"
              id="fullname"
            //   value={name}
            //   onChange={handleChange("name")}
