            <label htmlFor="phone_number">Phone Number</label>
            <input
              type="number"
              id="phone_number"
            //   value={phoneNumber}
            //   onChange={handleChange("phoneNumber")}
            />

            <label htmlFor="pass">Password</label>
            <input
              type="password"
              id="pass"
            //   value={password}
            //   onChange={handleChange("password")}
            />
