}; 
const HtnChangeHandler = (event) => {
  setEnteredHtn(event.target.value);
}; 
const DmChangeHandler = (event) => {
  setEnteredDm(event.target.value);
}; 
const CadChangeHandler = (event) => {
  setEnteredCad(event.target.value);
}; 
const AppetChangeHandler = (event) => {
  setEnteredAppet(event.target.value);
}; 
const PeChangeHandler = (event) => {
  setEnteredPe(event.target.value);
}; 
const AneChangeHandler = (event) => {
  setEnteredAne(event.target.value);
}; 



function handleSubmit(e) {  

e.preventDefault();
const submittingValue = {
  'age' : enteredAge,
  'bp' : enteredBp,
  'sg' : enteredSg,
  'al' : enteredAl,
  'su' : enteredSu,
  'rbc' : enteredRbc,
  'pc' : enteredPc,
  'pcc' : enteredPcc,
  'ba' : enteredBa,
  'bgr' : enteredBgr,
  'bu' : enteredBu,
  'sc' : enteredSc,
  'sod' : enteredSod,
  'pot' : enteredPot,
  'hemo' : enteredHemo,
  'pcv' : enteredPcv,
  'wc' : enteredWc,
  'rc' : enteredRc,
  'htn' : enteredHtn,
  'dm' : enteredDm,
  'cad' : enteredCad,
  'appet' : enteredAppet,
  'pe' : enteredPe,
  'ane' : enteredAne
};


setEnteredAge('');
setEnteredBp('');
setEnteredSg('');
setEnteredAl('');
setEnteredSu('');
setEnteredRbc('');
